import React from 'react';
import{Line} from 'react-chartjs-2'
function Linechart(){

    const data={
        labels :['jan','fab','mars','april','may'],
        datasets :[
            {
                label:'Sales for 2020(M)',
                data:[3,2,2,1,5]
            }
        ]
    }
    const options={
        title :{
            display:true,
            text:'Line chart'
        },
        scales :{
            yAxes :[
                {
                    ticks:{
                        min:0,
                        max:6,
                        stepSize:1
                    }
                }
            ]
        }
    }
    return(
        <div className="bodyChart">
       <Line data={data}/>
       </div>
    )
}
export default Linechart;
